import React, {Component} from 'react';


class RenderInfo extends Component {

    constructor(props){
        super(props);
        this.state = {
           pat:props.allpatArr,
           id:props.pat.id,
           name:'',
           age:'',
           gender: '',
           Disease: '',
           address:'',
           phone: ''
        }
    } 

    componentDidMount(){
        const url = `http://localhost:3000/cd/reqProduct?request_id=${this.state.id}`;
        fetch(url)
            .then(response => response.json())
            .then(data => {
                console.log(data);
            })
            .catch(error => console.log(error));
    }

    handelChange(event){
        const currentInput = event.target.name;
        const newValue = event.target.value;
        this.setState({
            [currentInput]: newValue
        }, () => {
            console.log(this.state);
        })
    }



    deletedd(id){
        const url = `http://localhost:3000/patient/${id}`;
        fetch(url, {
            method: 'DELETE'
          })
          .then(response => response.json())
          .then(data => {
              console.log(data);
              const updatePatient = this.state.req.filter( el => el.id !== id);

              this.props.allpat(updatePatient);
          })
          .catch(error => {
            console.log(error);
          })
    }

    deleteFunction(id){

        const url1 = `http://localhost:3000/patient/${id}`;
        fetch(url1, {
            method: 'DELETE'
          })
          .then(response => response.json())
          .then(data => {
              console.log(data);
              this.deletedd(id);


          })
          .catch(error => {
            console.log(error);
          })
    }

    render(){
        return(

            <div className="renderInformation">
            <h1>Render patient Information page</h1>


         <p>{this.props.pat.name}</p>
         <p>{this.props.pat.age}</p>
             <div>
             <button onClick={()=>this.deleteFunction(this.props.pat.id)}>Delete</button>
             </div>
             </div>
        )}}
export default RenderInfo;
