 import React, {Component} from 'react';
 class PatientProfile extends Component {

    constructor(props){
        super(props);
        this.state ={
            Name: '',
            Email: '',
            Password: '',


        }
    }
    handelChange(event){
        const currentInput = event.target.name;
        const newValue = event.target.value;
        this.setState({
            [currentInput]: newValue
        }, () => {
            console.log(this.state);

        })
    }
    handelSubmit(event){
        event.preventDefault();
        const patient ={
       Name:this.state.name,
       Email:this.state.email,
       Password:this.state.password

        }

        const url ='http://localhost:3000/patient';
        fetch(url, {
            method: 'POST',
            headers: {
            "Content-Type": "application/json"
            },
            body: JSON.stringify(patient)
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // this.setState({

                // }, () => {
                // })
            })
            .catch(error => console.log(error));
    }

    render(){
        return(
            <div className="patientInfo">
            <h1> Patient Information</h1>
            <form onSubmit={this.handelSubmit.bind(this)}>

                <label>Name: </label><input type="text" name="Name" onChange={this.handelChange.bind(this)}/><br/>
                <label>email: </label><input type="number" name="Age" onChange={this.handelChange.bind(this)}/><br/>
                <label>password: </label><input type="gender" name="gender" onChange={this.handelChange.bind(this)}/><br/>

                <br/>

                <button >Submit</button>
                {/* onClick={() => props.setActivePage('app')} */}
            </form>
        </div>
        )
    }
}

export default PatientProfile;















































