import React, { Component } from 'react';
import RenderInfo from './RenderInfo';

class PatientInfo extends Component{
  constructor(props){
    super(props);
    this.state = {
        patients:[],
        Id:props.patientInfo.patient ? props.patientInfo.patient.id: '',
        Name: props.patientInfo.patient ? props.patientInfo.patient.name: '',
        Age:props.patientInfo.patient ? props.patientInfo.patient.Age: '' ,
        Gender: props.patientInfo.patient ? props.patientInfo.patient.Gender: '',
        Disease: props.patientInfo.patient ? props.patientInfo.patient.Disease: '',
        Address:props.patientInfo.patient ? props.patientInfo.patient.Address: '',
        Phone: props.patientInfo.patient ? props.patientInfo.patient.Phone: ''  

        }
    }

    update(id, patientData) {
        const url = `http://localhost:3000/patient/${id}`
        fetch(url, {
          method: 'PUT',
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(patientData)
        })
        .then(response => response.json())
        .then(data => {
          console.log(data);

        })
        .catch(error => {
          console.log(error);
        })

  }
     handelChange(event){
      this.setState({ [event.target.name] : event.target.value })
     }

     handelSubmit(event){
        event.preventDefault();

        const patientInfo = {
          Name: this.state.Name,
          Age: this.state.Age,
          Gender: this.state.Gender,
          Disease: this.state.Disease,
          Address: this.state.Address,
          Phone: this.state.Phone
        }
        this.update(this.state.id, patientInfo);
     }
     setAllpat(allpat){
        this.setState({
            patients: allpat
        })
     }
     renderPatient(allPatient) {
       console.log(allPatient);
        return allPatient.map((pat) => {
          return (
            <RenderInfo
            key={pat.id}
            patient={pat}
            allpat={this.setAllpat.bind(this)}
            allpatArr={this.state.pat}
            />
          )
        })
      }
    renderInformation(){
        console.log('render information');

        return (
          <div className="pateint lInfo"> 
              <h1>patient Information</h1>
              <form onSubmit={this.handelSubmit.bind(this)}>

                <label>Name: </label><input type="text" name="Name" onChange={this.handelChange.bind(this)} value={this.state.Name}/><br/>
                <label>Age: </label><input type="number" name="Age" onChange={this.handelChange.bind(this)} value={this.state.Age}/><br/>
                <label>Gender: </label><input type="gender" name="gender" onChange={this.handelChange.bind(this)} value={this.state.Gender}/><br/>
                <label>Disease: </label><input type="text" name="Disease" onChange={this.handelChange.bind(this)} value={this.state.Disease}/><br/>
                <label>Address: </label><input type="text" name="Adress" onChange={this.handelChange.bind(this)} value={this.state.Address}/><br/>
                <label>Phone: </label><input type="text" name="Phone" onChange={this.handelChange.bind(this)} value={this.state.Phone}/><br/>
                <button>Edit</button>
            </form>
            <div>
            </div>
          </div>
        )
    }
    render(){
        return (
            <div>
                {this.renderInformation()}
                {this.renderPatient(this.state.pat)}
            </div>
        )
    }
  }
export default PatientInfo;
