import React, {Component} from 'react';

class PatientProfile extends Component {

    constructor(props){
        super(props);
        this.state ={
            Name: '',
            Age: 0,
            Gender: '',
            Disease: '',
            Address:'',
            Phone:''

        }
    }

    handelChange(event){
        const currentInput = event.target.name;
        const newValue = event.target.value;
        this.setState({
            [currentInput]: newValue
        }, () => {
            console.log(this.state);

        })
    }
    handelSubmit(event){
        event.preventDefault();
        const patient ={
    Name:this.state.name,
    Age:this.state.age,
    Gender:this.state.gender,
    Disease:this.state.Disease,
    Address: this.state.address,
    Phone: this.state.phone

        }
        const url ='http://localhost:3000/patient';
        fetch(url, {
            method: 'POST',
            headers: {
            "Content-Type": "application/json"
            },
            body: JSON.stringify(patient)
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // this.setState({
                //    patientID: data.patient.id,
                // }, () => {
                // })
            })
            .catch(error => console.log(error));
    }

    render(){
        return(
            <div className="patientInfo">
            <h1> Patient Information</h1>
            <form onSubmit={this.handelSubmit.bind(this)}>
                <label>Name: </label><input type="text" name="Name" onChange={this.handelChange.bind(this)}/><br/>
                <label>Age: </label><input type="number" name="Age" onChange={this.handelChange.bind(this)}/><br/>
                <label>Gender: </label><input type="gender" name="gender" onChange={this.handelChange.bind(this)}/><br/>
                <label>Disease: </label><input type="text" name="Disease" onChange={this.handelChange.bind(this)}/><br/>
                <label>Address: </label><input type="text" name="Adress" onChange={this.handelChange.bind(this)}/><br/>
                <label>Phone: </label><input type="text" name="Phone" onChange={this.handelChange.bind(this)}/><br/>
                <br/>
                <button >Submit</button>

                {/* onClick={() => props.setActivePage('app')} */}
            </form>
        </div>
        )
    }
}

export default PatientProfile;
